<?php	declare(strict_types=1);		namespace GambiEl\LiteralQuery\Filter;		class LiteralQueryFilter {		public static function CriarFiltro($ArrValues) {			$consulta = '';						foreach ($ArrValues as $key => $value):				if ($value != ''):					if ($consulta != ''):						$consulta .= " AND " . $key;					else:						$consulta = $key;					endif;				endif;			endforeach;						return $consulta;		}	}